//   }
//   render() {
//     return (
//       <button className="square"
//         onClick={() => this.props.onClick()}
//       >
//         {this.props.value}
//       </button>
//     ); // this lets us know which button was clicked and will change to X || 0
//   }
// }

function Square(props) {
  return (
    <button className="square" onClick={() => props.onClick}>
      {props.value}
    </button>
  );
}

//example of passing a prop, Board is parent, child is square

class Board extends React.Component {
  constructor(prop) {
    super(prop);
    this.state = {
      squares: Array(9).fill(null),
    };
  }
  handleClick(i) {
    const squares = this.state.squares.slice(); //slice to copy the array as oppose to modifying the existing one
    squares[i] = 'X';
    this.setState({ squares: squares });
  }
  renderSquare(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  } // parent is passing down Square two props, value & onclick

  render() {
    const status = "Next player: X";

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

/* ======================================== 
https://reactjs.org/tutorial/tutorial.html#setup-option-2-local-development-environment
*/
ReactDOM.render(<Game />, document.getElementById("root"));
